// AgendamentosPage.tsx
import React, { useEffect, useState } from 'react';
import { useAuth } from '../Context/useAuth'; // Importando o contexto para obter o token
import { getAgendamentosAPI } from '../api/AgendamentoAPI'; // Importando o serviço

const AgendamentosPage = () => {
    const { token } = useAuth(); // Obtendo o token do contexto
    const userId = 12; // Substitua isso pelo ID do usuário apropriado
    const [agendamentos, setAgendamentos] = useState<any[]>([]); // Estado para armazenar os agendamentos
    const [error, setError] = useState<string | null>(null); // Estado para armazenar erros

    useEffect(() => {
        const fetchAgendamentos = async () => {
            try {
                const data = await getAgendamentosAPI(token, userId); // Chamando a API para buscar os agendamentos
                setAgendamentos(data); // Armazenando os agendamentos no estado
            } catch (error) {
                setError('Erro ao buscar os agendamentos'); // Tratando erro
            }
        };

        if (token) { // Apenas buscar se o token estiver presente
            fetchAgendamentos(); // Chamando a função de busca
        }
    }, [token]); // Executa o efeito quando o token muda

    return (
        <div>
            <h1>Meus Agendamentos</h1>
            {error && <p className="error-message">{error}</p>}
            <ul>
                {agendamentos.map((agendamento) => (
                    <li key={agendamento.id}>{agendamento.nome}</li> // Ajuste de acordo com os dados que você recebe
                ))}
            </ul>
        </div>
    );
};

export default AgendamentosPage;
