import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import CampoTexto from './componentes/CampoTexto';

interface ServicoSubCategoria {
  id: number;
  nome: string;
  duracao: string; // formato "HH:mm:ss"
  valor: number;
}

function App() {
  const [services, setServices] = useState<ServicoSubCategoria[]>([]);
  const [nome, setNome] = useState('');
  const [duracao, setDuracao] = useState('');
  const [valor, setValor] = useState('');

  // Função para buscar serviços do backend
  const fetchServices = async () => {
    try {
      const response = await axios.get<ServicoSubCategoria[]>('https://localhost:7265/api/SSubCategorias');
      setServices(response.data);
    } catch (error) {
      console.error("Erro ao buscar serviços:", error);
    }
  };

  useEffect(() => {
    fetchServices();
  }, []);

  // Função de criação do serviço
  const handleCreateService = async () => {
    if (!nome || !duracao || !valor) {
      alert('Preencha todos os campos');
      return;
    }

    const novoServico = {
      nome,
      duracao,
      valor: parseFloat(valor),
    };

    try {
      const response = await axios.post('https://localhost:7265/api/SSubCategorias', novoServico);
      const servicoCriado = response.data;

      // Atualizar a lista de serviços
      setServices([...services, servicoCriado]);

      // Limpar os campos
      setNome('');
      setDuracao('');
      setValor('');
    } catch (error) {
      console.error("Erro ao criar serviço:", error);
    }
  };

  return (
    <div className="App">
      <header className="header">
        <div className="logo">BelezaMarket</div>
        <button className="account-button">Minha conta</button>
      </header>
      <main className="content">
        <section className="form-section">
          <h2>Cadastre um novo serviço</h2>
          <form onSubmit={(e) => e.preventDefault()}>
            <input
              type="text"
              placeholder="Digite o nome do serviço"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
            <input
              type="text"
              placeholder="Digite a duração do serviço (HH:mm:ss)"
              value={duracao}
              onChange={(e) => setDuracao(e.target.value)}
            />
            <input
              type="text"
              placeholder="Digite o preço do serviço"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
            <div className="button-group">
              <button type="button" onClick={handleCreateService}>Criar Serviço</button>
            </div>
          </form>
        </section>
        <section className="services-list">
          <h3>Serviços Cadastrados:</h3>
          {services.length === 0 ? (
            <p>Nenhum serviço cadastrado.</p>
          ) : (
            <ul>
              {services.map((service) => (
                <li key={service.id}>
                  <h4>{service.nome}</h4>
                  <p>Duração: {formatDuration(service.duracao)}</p>
                  <p>Preço: R${service.valor.toFixed(2)}</p>
                </li>
              ))}
            </ul>
          )}
        </section>
      </main>
    </div>
  );
}

// Função auxiliar para formatar a duração
const formatDuration = (duration: string) => {
  const [hours, minutes] = duration.split(':');
  return `${hours}h ${minutes}min`;
};

export default App;
