import React, { useEffect, useState } from 'react';
import { useAuth } from '../Context/useAuth';
import { getAgendamentosAPI } from '../api/AgendamentoAPI';
import Header from '../../Components/Header'; // Adjust the path as needed
import { FaTrash, FaEdit, FaTimes } from 'react-icons/fa'; // Importing icons from react-icons/fa
import '../Agendamentos/AgendamentoPage.css';

const AgendamentosPage: React.FC = () => {
    const { token, user } = useAuth(); // Get token and user info from context
    const userId = user?.userId; // Safely access userId using optional chaining
    const [agendamentos, setAgendamentos] = useState<any[]>([]);
    const [loading, setLoading] = useState<boolean>(true); // Loading state
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchAgendamentos = async () => {
            const locarlStorageToken = localStorage.getItem("token");
            const storedUserId = Number(localStorage.getItem("userId"));

            // If no token or userId, don't fetch
            if (!locarlStorageToken || !storedUserId) {
                setError('Token ou ID de usuário ausente.');
                setLoading(false);
                return;
            }

            try {
                const data = await getAgendamentosAPI(locarlStorageToken, storedUserId);
                setAgendamentos(data);
                console.log("Fetched Agendamentos:", data); // Log fetched data for debugging
            } catch (error) {
                setError('Erro ao buscar os agendamentos');
                console.error("Error fetching agendamentos:", error); // Log error details for debugging
            } finally {
                setLoading(false); // Always set loading to false after fetching
            }
        };

        fetchAgendamentos();
    }, [token, userId]); // Fetch when token or userId changes

    // Handler functions for the actions (delete, edit, cancel)
    const handleDelete = (id: number) => {
        console.log(`Deleting agendamento with id: ${id}`);
        // Call the delete API here
    };

    const handleEdit = (id: number) => {
        console.log(`Editing agendamento with id: ${id}`);
        // Call the edit API here or navigate to the edit page
    };

    const handleCancel = (id: number) => {
        console.log(`Cancelling agendamento with id: ${id}`);
        // Call the cancel API here
    };

    // Function to check if the agendamento is in the past
    const isPastAgendamento = (dataAgendamento: string) => {
        const today = new Date();
        const agendamentoDate = new Date(dataAgendamento);
        return agendamentoDate < today;
    };

    // Render loading state or error message
    if (loading) {
        return <p>Carregando seus agendamentos...</p>;
    }

    return (
        <div className="agendamentos-page">
            <Header /> {/* Includes the header */}
            <main className="main-content">
                <h1>Meus Agendamentos</h1>
                {error && <p className="error-message">{error}</p>}
                {agendamentos.length === 0 ? (
                    <p>Você não tem agendamentos no momento.</p>
                ) : (
                    <div className="agendamentos-cards-container">
                        {agendamentos.map((agendamento) => {
                            const isPast = isPastAgendamento(agendamento.dataAgendamento);

                            return (
                                <div
                                    className={`agendamento-card ${isPast ? 'past-agendamento' : ''}`}
                                    key={agendamento.id}
                                >
                                    <div className="card-header">
                                        <h3>{agendamento.servicoSubCategoria.nome}</h3>
                                        <div className="action-icons">
                                            <FaEdit
                                                className="edit-icon"
                                                title="Editar Agendamento"
                                                onClick={!isPast ? () => handleEdit(agendamento.id) : undefined}
                                                style={{ pointerEvents: isPast ? 'none' : 'auto' }}
                                            />
                                            <FaTrash
                                                className="delete-icon"
                                                title="Deletar Agendamento"
                                                onClick={!isPast ? () => handleDelete(agendamento.id) : undefined}
                                                style={{ pointerEvents: isPast ? 'none' : 'auto' }}
                                            />
                                            <FaTimes
                                                className="cancel-icon"
                                                title="Cancelar Agendamento"
                                                onClick={!isPast ? () => handleCancel(agendamento.id) : undefined}
                                                style={{ pointerEvents: isPast ? 'none' : 'auto' }}
                                            />
                                        </div>
                                    </div>
                                    <div className="card-body">
                                        <p><strong>Data:</strong> {new Date(agendamento.dataAgendamento).toLocaleDateString()}</p>
                                        <p><strong>Hora:</strong> {agendamento.horaAgendamento}</p>
                                        <p><strong>Status:</strong> {agendamento.status}</p>
                                        <p><strong>Usuário ID:</strong> {agendamento.usuarioId}</p>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                )}
            </main>

            <footer className="footer">
                <p>&copy; 2024 BelezaMarket - Todos os direitos reservados.</p>
            </footer>
        </div>
    );
};

export default AgendamentosPage;
