// AgendamentosPage.tsx
import React, { useEffect, useState } from 'react';
import { useAuth } from '../Context/useAuth';
import { getAgendamentosAPI } from '../api/AgendamentoAPI';
import '../Agendamentos/AgendamentoPage.css';

const AgendamentosPage: React.FC = () => {
    const { token, user } = useAuth(); // Get token and user info from context
    const userId = user?.userId; // Safely access userId using optional chaining
    const [agendamentos, setAgendamentos] = useState<any[]>([]);
    const [loading, setLoading] = useState<boolean>(true); // Loading state
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchAgendamentos = async () => {
            // Log token and userId to debug
           
            console.log(localStorage.getItem("token"));
            console.log(localStorage.getItem("userId"))

            const locarlStorageToken = localStorage.getItem("token")
            const storedUserId = Number(localStorage.getItem("userId"));

            // If no token or userId, don't fetch
            if (!locarlStorageToken || !storedUserId) {
                setError('Token ou ID de usuário ausente.');
                setLoading(false);
                return;
            }

            try {
                const data = await getAgendamentosAPI(locarlStorageToken, storedUserId);
                setAgendamentos(data);
                console.log("Fetched Agendamentos:", data); // Log fetched data for debugging
            } catch (error) {
                setError('Erro ao buscar os agendamentos');
                console.error("Error fetching agendamentos:", error); // Log error details for debugging
            } finally {
                setLoading(false); // Always set loading to false after fetching
            }
        };

        fetchAgendamentos();
    }, [token, userId]); // Fetch when token or userId changes

    // Render loading state or error message
    if (loading) {
        return <p>Carregando seus agendamentos...</p>;
    }

    return (
        <div className="container">
            <h1>Meus Agendamentos</h1>
            {error && <p className="error-message">{error}</p>}
            {agendamentos.length === 0 ? (
                <p>Você não tem agendamentos no momento.</p>
            ) : (
                <table className="agendamentos-table">
                <thead>
                    <tr>
                        <th>Usuário ID</th>
                        <th>Serviço</th>
                        <th>Data do Agendamento</th>
                        <th>Hora do Agendamento</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {agendamentos.map((agendamento) => (
                        <tr key={agendamento.id}>
                            <td>{agendamento.usuarioId}</td>
                            <td>{agendamento.servicoSubCategoria.nome}</td>
                            <td>{new Date(agendamento.dataAgendamento).toLocaleDateString()}</td>
                            <td>{agendamento.horaAgendamento}</td>
                            <td>{agendamento.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            )}
        </div>
    );
};

export default AgendamentosPage;
