// Pages/LoginPage/LoginPage.tsx
import React, { useState } from 'react';
import { useAuth } from '../Context/UserProvider'; // Import the useAuth hook
import './LoginPage.css'; // Import the CSS file

const LoginPage: React.FC = () => {
    const { loginUser } = useAuth(); // Use the loginUser function from context
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [errorMessage, setErrorMessage] = useState<string | null>(null); // Optional: for error messages

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await loginUser(email, senha); // Call the loginUser function
        } catch (error) {
            setErrorMessage("Login failed. Please check your credentials."); // Update with your error handling logic
        }
    };

    return (
      <div className="login-section">
          <div className="login-container">
              <div className="login-card">
                  <div className="login-content">
                      <h1 className="login-title">Login</h1>
                      <form className="login-form" onSubmit={handleSubmit}>
                          <div className="form-group">
                              <label className="form-label">Email:</label>
                              <input
                                  type="email"
                                  className="form-input"
                                  value={email}
                                  onChange={(e) => setEmail(e.target.value)}
                                  required
                              />
                          </div>
                          <div className="form-group">
                              <label className="form-label">Senha:</label>
                              <input
                                  type="password"
                                  className="form-input"
                                  value={senha}
                                  onChange={(e) => setSenha(e.target.value)}
                                  required
                              />
                          </div>
                          {errorMessage && <p className="error-message">{errorMessage}</p>}
                          <button type="submit" className="button">Entrar</button>
                          <p className="forgot-password">Esqueceu a senha?</p>
                      </form>
                      <p className="signup-prompt">
                          NÃ£o tem uma conta? <span className="signup-link">Cadastre-se</span>
                      </p>
                  </div>
              </div>
          </div>
      </div>
  );
};  
export default LoginPage; // Export LoginPage as default
