// AgendamentosPage.tsx
import React, { useEffect, useState } from 'react';
import { useAuth } from '../Context/useAuth';
import { getAgendamentosAPI } from '../api/AgendamentoAPI';

const AgendamentosPage = () => {
    const { token, user } = useAuth(); // Now also get user
    const userId = user?.userId; // Use optional chaining to safely access userId
    const [agendamentos, setAgendamentos] = useState<any[]>([]);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchAgendamentos = async () => {
            if (!token || !userId) return; // If no token or userId, don't fetch
            try {
                const data = await getAgendamentosAPI(token, userId);
                setAgendamentos(data);
            } catch (error) {
                setError('Erro ao buscar os agendamentos');
            }
        };

        fetchAgendamentos();
    }, [token, userId]); // Fetch when token or userId changes

    return (
        <div>
            <h1>Meus Agendamentos</h1>
            {error && <p className="error-message">{error}</p>}
            <ul>
                {agendamentos.map((agendamento) => (
                    <li key={agendamento.id}>{agendamento.servicoSubCategoria.nome} - {agendamento.dataAgendamento} {agendamento.horaAgendamento} - Status: {agendamento.status}</li>
                ))}
            </ul>
        </div>
    );
};

export default AgendamentosPage;
