import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // Arquivo CSS para estilos

const HomePage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Função para tratar o login
  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Email:', email);
    console.log('Password:', password);

    // Simular login e redirecionar para a página principal após sucesso
    navigate('/dashboard'); // Redireciona para o dashboard ou outra página
  };

  return (
    <div className="home-container">
      <header className="header">
        <h1>Bem-vindo ao Salão de Beleza</h1>
        <p>Cuide da sua beleza com nossos serviços profissionais!</p>
      </header>

      <section className="login-section">
        <h2>Faça seu login</h2>
        <form className="login-form" onSubmit={handleLogin}>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Digite seu email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label htmlFor="password">Senha:</label>
          <input
            type="password"
            id="password"
            name="password"
            placeholder="Digite sua senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <input type="submit" value="Entrar" />
        </form>

        <button className="btn" onClick={() => navigate('/cadastro')}>
          Não tem conta? Cadastre-se
        </button>
      </section>
    </div>
  );
};

export default HomePage;
