// Pages/LoginPage/LoginPage.tsx
import React, { useState } from 'react';
import { useAuth } from '../Context/UserProvider'; // Import the useAuth hook

const LoginPage: React.FC = () => {
    const { loginUser } = useAuth(); // Use the loginUser function from context
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        await loginUser(email, senha); // Call the loginUser function
    };

    return (
        <div>
            <h1>Login Page</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Senha:</label>
                    <input
                        type="password"
                        value={senha}
                        onChange={(e) => setSenha(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default LoginPage; // Export LoginPage as default
