// Context/UserProvider.tsx
import { createContext, useEffect, useState } from "react";
import { UserProfile } from "../Models/User"; // Ensure this type includes only the necessary properties
import { useNavigate } from "react-router-dom";
import { loginAPI } from "../Services/AuthService";
import { toast } from "react-toastify";
import React from "react";
import '../LoginPage/LoginPage.css';
import axios from "axios";

type UserContextType = {
    user: UserProfile | null;
    token: string | null;
    loginUser: (email: string, senha: string) => Promise<void>; // Added Promise return type
    logout: () => void;
    isLoggedIn: () => boolean;
};

type Props = { children: React.ReactNode };

const UserContext = createContext<UserContextType>({} as UserContextType);

export const UserProvider = ({ children }: Props) => {
    const navigate = useNavigate();
    const [token, setToken] = useState<string | null>(null);
    const [user, setUser] = useState<UserProfile | null>(null);
    const [isReady, setIsReady] = useState(false);

    // Load stored token and user data from local storage on initial mount
    useEffect(() => {
        const storedUser = localStorage.getItem("user");
        const storedToken = localStorage.getItem("token");

        if (storedUser && storedToken) {
            setUser(JSON.parse(storedUser));
            setToken(storedToken);
            axios.defaults.headers.common["Authorization"] = `Bearer ${storedToken}`;
        }
        setIsReady(true); // Indicate that the user context is ready
    }, []);

    // Login function
    const loginUser = async (email: string, senha: string) => {
        try {
            const res = await loginAPI(email, senha);

            if (res && res.data) {
                const { token, userId } = res.data; // Removed redundant email since it can be derived

                // Save token and user data to local storage
                localStorage.setItem("token", token);
                const userObj: UserProfile = { email, senha, userId }; // Removed senha for security reasons

                // Set state and axios authorization header
                setToken(token);
                setUser(userObj); // This should match the UserProfile structure
                axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

                toast.success("Login Successful!");
                navigate("/agendamentos"); // Redirect to Agendamentos page after successful login
            }
        } catch (error: any) {
            const errorMessage = getErrorMessage(error);
            toast.error(errorMessage);
        }
    };

    // Function to get error message
    const getErrorMessage = (error: any): string => {
        if (error.response) {
            console.error("Login error:", error.response.data);
            return "Login failed: " + error.response.data.message;
        } else if (error.request) {
            console.error("No response received:", error.request);
            return "No response from server. Please try again later.";
        } else {
            console.error("Error:", error.message);
            return "An error occurred during login.";
        }
    };

    // Check if user is logged in
    const isLoggedIn = () => !!user;

    // Logout function
    const logout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        setUser(null);
        setToken(null);
        delete axios.defaults.headers.common["Authorization"]; // Clear the authorization header
        navigate("/"); // Redirect to homepage after logout
    };

    return (
        <UserContext.Provider
            value={{ loginUser, user, token, logout, isLoggedIn }}
        >
            {isReady ? children : null}
        </UserContext.Provider>
    );
};

// Hook to use the authentication context
export const useAuth = () => React.useContext(UserContext);
 
// Your LoginPage component definition
export const LoginPage: React.FC = () => {
  return (
      <div>
          <h1>Login Page</h1>
          {/* Add your login form here */}
      </div>
  );
};
