import axios from 'axios';

export const loginAPI = async (email: string, senha: string) => {
  try {
    const response = await axios.post('https://localhost:5001/gateway/Usuarios/authentication', {
      email,
      senha
    });
    return response; // Return the response object
  } catch (error) {
    throw error; // Handle errors in useAuth.tsx
  }
};
