// router.tsx (or similar file where you define your routes)
import { createBrowserRouter } from "react-router-dom";
import App from "../App";
import HomePage from "../Pages/HomePage/HomePage";
import RegisterPage from "../Pages/RegisterPage/RegisterPage";
import ProtectedRoute from "./ProtectedRoute";
import AgendamentoPage from "../Pages/Agendamentos/AgendamentoPage";
import LoginPage from "../Pages/LoginPage/LoginPage"; // Import LoginPage here
import React from 'react';
import { UserProvider } from '../Pages/Context/useAuth'; // Import UserProvider here

// Create the router
export const router = createBrowserRouter([
  {
    path: "/",
    element: (
      <UserProvider> {/* Wrap the App with UserProvider */}
        <App />
      </UserProvider>
    ),
    children: [
      { path: "", element: <HomePage /> },
      { path: "login", element: <LoginPage /> }, // Ensure LoginPage is defined
      { path: "register", element: <RegisterPage /> },
      { path: "agendamento", element: <AgendamentoPage /> },
    ],
  },
]);
