import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import CampoTexto from './componentes/CampoTexto';

function App() {
  const [services, setServices] = useState([]);

  // Função para buscar serviços do backend
  const fetchServices = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/SSubCategorias');
      setServices(response.data);
    } catch (error) {
      console.error("Erro ao buscar serviços:", error);
    }
  };

  useEffect(() => {
    fetchServices(); // Buscar serviços quando o componente é montado
  }, []);

  const handleCreateService = () => {
    // Função para adicionar serviço (a implementar)
  };

  return (
    <div className="App">
      <header className="header">
        <div className="logo">BelezaMarket</div>
        <button className="account-button">Minha conta</button>
      </header>
      <main className="content">
        <section className="form-section">
          <h2>Cadastre um novo serviço</h2>
          <form>
            <input type="text" placeholder="Digite o nome do serviço" />
            <input type="text" placeholder="Digite a duração do serviço" />
            <input type="text" placeholder="Digite o preço do serviço" />
            <div className="button-group">
              <button type="button" onClick={handleCreateService}>Criar Serviço</button>
              <button type="button" onClick={fetchServices}>Puxar Serviços</button>
            </div>
          </form>
        </section>
        <section className="services-list">
          <h3>Serviços Cadastrados:</h3>
          {services.length === 0 ? (
            <p>Nenhum serviço cadastrado.</p>
          ) : (
            <ul>
              {services.map((service) => (
                <li key={service.id}>
                  <strong>{service.nome}</strong> - 
                  Duração: {formatDuration(service.duracao)} - 
                  Preço: R${service.valor.toFixed(2)}
                </li>
              ))}
            </ul>
          )}
        </section>
      </main>
    </div>
  );
}

// Função auxiliar para formatar a duração (TimeSpan) em horas e minutos
const formatDuration = (duration) => {
  const [hours, minutes] = duration.split(':');
  return `${hours}h ${minutes}min`;
};

export default App;
